// Analyser les rÃ©sultats des tests et du linting
const ciOutput = $node['Run CI Tests'].json.stdout || '';
const linterOutput = $node['Run Code Linter'].json.stdout || '';

// Extraire les rÃ©sultats des tests CI
let ciResults = { success: false, failures: [] };
try {
  const ciMatch = ciOutput.match(/\{[\s\S]*?\}/);
  if (ciMatch) {
    const ciData = JSON.parse(ciMatch[0]);
    ciResults = {
      success: ciData.success || false,
      failures: ciData.failures || [],
      testsPassed: ciData.testsPassed || 0,
      testsFailed: ciData.testsFailed || 0,
      coverage: ciData.coverage || '0%'
    };
  }
} catch (error) {
  ciResults.failures.push(`Error parsing CI results: ${error.message}`);
}

// Extraire les rÃ©sultats du linting
let lintResults = { success: false, errors: [] };
try {
  const lintMatch = linterOutput.match(/\{[\s\S]*?\}/);
  if (lintMatch) {
    const lintData = JSON.parse(lintMatch[0]);
    lintResults = {
      success: lintData.success || false,
      errors: lintData.errors || [],
      warnings: lintData.warnings || [],
      errorCount: lintData.errorCount || 0,
      warningCount: lintData.warningCount || 0,
      fixableErrorCount: lintData.fixableErrorCount || 0
    };
  }
} catch (error) {
  lintResults.errors.push(`Error parsing linter results: ${error.message}`);
}

// Consolider les rÃ©sultats
const pipelineSuccess = ciResults.success && lintResults.success;
const summary = {
  success: pipelineSuccess,
  testsPassed: ciResults.testsPassed || 0,
  testsFailed: ciResults.testsFailed || 0,
  coverage: ciResults.coverage || '0%',
  lintErrors: lintResults.errorCount || 0,
  lintWarnings: lintResults.warningCount || 0,
  lintFixable: lintResults.fixableErrorCount || 0,
  timestamp: new Date().toISOString()
};

// GÃ©rer les pull requests et notifications
let prData = {};
if ($json.body && $json.body.pull_request) {
  prData = {
    repo: $json.body.repository.full_name,
    prNumber: $json.body.pull_request.number,
    prTitle: $json.body.pull_request.title,
    prAuthor: $json.body.pull_request.user.login,
    prUrl: $json.body.pull_request.html_url
  };
}

return {
  json: {
    ...summary,
    ciResults,
    lintResults,
    prData,
    detailedReport: {
      ciOutput: ciOutput.substring(0, 500) + (ciOutput.length > 500 ? '...' : ''),
      linterOutput: linterOutput.substring(0, 500) + (linterOutput.length > 500 ? '...' : '')
    }
  }
};

