// Dﾃｩfinir les seuils d'alerte
return {
  complexityThreshold: 15,  // Seuil de complexitﾃｩ cyclomatique considﾃｩrﾃｩ comme ﾃｩlevﾃｩ
  issuesThreshold: 10,      // Seuil de problﾃｨmes considﾃｩrﾃｩ comme ﾃｩlevﾃｩ
  sendEmail: true,          // Envoyer des alertes par email
  sendSlack: true,          // Envoyer des alertes sur Slack
  alertTitle: "泅ｨ Alerte de qualitﾃｩ de code PHP",
  recipients: [
    "team-dev@example.com",
    "tech-lead@example.com"
  ],
  slackWebhookUrl: process.env.SLACK_WEBHOOK_PHP_QUALITY || "",
  repoUrl: "https://github.com/organization/project/blob/main/" // URL de base du rﾃｩpertoire GitHub
};

// Formater les donnﾃｩes pour le rapport
const files = $input.all();
const thresholds = $node["Set Alert Thresholds"].json;

// Crﾃｩer un tableau HTML pour le rapport
let tableHtml = `
<table style="border-collapse: collapse; width: 100%; margin-top: 20px;">
  <thead>
    <tr style="background-color: #f2f2f2;">
      <th style="border: 1px solid #ddd; padding: 8px; text-align: left;">Fichier</th>
      <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Complexitﾃｩ</th>
      <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Maintenabilitﾃｩ</th>
      <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Problﾃｨmes</th>
      <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Lignes</th>
      <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Analysﾃｩ le</th>
    </tr>
  </thead>
  <tbody>
`;

files.forEach(file => {
  // Dﾃｩfinir les couleurs en fonction des seuils
  const complexityColor = file.complexity > thresholds.complexityThreshold ? "#f44336" : file.complexity > 10 ? "#ff9800" : "#4caf50";
  const maintainabilityColor = file.maintainability < 50 ? "#f44336" : file.maintainability < 70 ? "#ff9800" : "#4caf50";
  const issuesColor = file.issues_count > thresholds.issuesThreshold ? "#f44336" : file.issues_count > 5 ? "#ff9800" : "#4caf50";

  // Formater la date
  const date = new Date(file.analyzed_at).toLocaleString('fr-FR', {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  });

  // Construire l'URL GitHub pour le fichier
  const githubUrl = thresholds.repoUrl + file.path;

  // Ajouter la ligne au tableau
  tableHtml += `
    <tr>
      <td style="border: 1px solid #ddd; padding: 8px;">
        <a href="${githubUrl}" target="_blank">${file.filename}</a>
      </td>
      <td style="border: 1px solid #ddd; padding: 8px; text-align: center; background-color: ${complexityColor}; color: white; font-weight: bold;">
        ${file.complexity}
      </td>
      <td style="border: 1px solid #ddd; padding: 8px; text-align: center; background-color: ${maintainabilityColor}; color: white; font-weight: bold;">
        ${file.maintainability}
      </td>
      <td style="border: 1px solid #ddd; padding: 8px; text-align: center; background-color: ${issuesColor}; color: white; font-weight: bold;">
        ${file.issues_count}
      </td>
      <td style="border: 1px solid #ddd; padding: 8px; text-align: center;">
        ${file.lines_of_code}
      </td>
      <td style="border: 1px solid #ddd; padding: 8px; text-align: center;">
        ${date}
      </td>
    </tr>
  `;
});

tableHtml += `
  </tbody>
</table>
`;

// Gﾃｩnﾃｩrer le contenu de l'email
const emailHtml = `
<div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto;">
  <h2 style="color: #d9534f;">${thresholds.alertTitle}</h2>

  <p>L'analyse de code PHP a dﾃｩtectﾃｩ <strong>${files.length} fichiers</strong> dﾃｩpassant les seuils de complexitﾃｩ et/ou de problﾃｨmes :</p>

  <ul>
    <li>Complexitﾃｩ cyclomatique > ${thresholds.complexityThreshold}</li>
    <li>Nombre de problﾃｨmes > ${thresholds.issuesThreshold}</li>
  </ul>

  <p>Ces fichiers nﾃｩcessitent une attention particuliﾃｨre pour amﾃｩliorer la qualitﾃｩ du code et faciliter la maintenance.</p>

  ${tableHtml}

  <div style="margin-top: 30px; padding-top: 20px; border-top: 1px solid #eee;">
    <p style="color: #666; font-size: 0.9em;">
      Vous recevez cette alerte automatique car vous ﾃｪtes inscrit aux notifications de qualitﾃｩ de code.<br>
      Pour visualiser les dﾃｩtails complets, consultez le <a href="https://dashboard.example.com/php-analysis" target="_blank">Dashboard d'analyse PHP</a>.
    </p>
  </div>
</div>
`;

// Gﾃｩnﾃｩrer le contenu pour Slack
let slackBlocks = [
  {
    "type": "header",
    "text": {
      "type": "plain_text",
      "text": thresholds.alertTitle,
      "emoji": true
    }
  },
  {
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": `*${files.length} fichiers PHP* dﾃｩpassent les seuils de qualitﾃｩ (complexitﾃｩ > ${thresholds.complexityThreshold} ou problﾃｨmes > ${thresholds.issuesThreshold})`
    }
  },
  {
    "type": "divider"
  }
];

// Ajouter les 5 premiers fichiers les plus problﾃｩmatiques ﾃ Slack
files.slice(0, 5).forEach(file => {
  slackBlocks.push({
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": `*<${thresholds.repoUrl + file.path}|${file.filename}>*\nComplexitﾃｩ: ${file.complexity} | Problﾃｨmes: ${file.issues_count} | Maintenabilitﾃｩ: ${file.maintainability}`
    }
  });
});

// Ajouter un lien vers le dashboard
slackBlocks.push({
  "type": "section",
  "text": {
    "type": "mrkdwn",
    "text": "Consultez le <https://dashboard.example.com/php-analysis|Dashboard d'analyse PHP> pour plus de dﾃｩtails."
  }
});

return {
  emailHtml,
  slackBlocks,
  filesCount: files.length,
  recipients: thresholds.recipients,
  sendEmail: thresholds.sendEmail,
  sendSlack: thresholds.sendSlack,
  slackWebhookUrl: thresholds.slackWebhookUrl
};

// Enregistrer dans les logs n8n que nous n'avons pas trouvﾃｩ de fichiers complexes
return {
  message: "Aucun fichier PHP complexe dﾃｩtectﾃｩ au-dessus des seuils configurﾃｩs.",
  thresholds: $node["Set Alert Thresholds"].json
};

// Formater les donnﾃｩes pour le rapport hebdomadaire
const data = $input.item.json;
const thresholds = $node["Set Alert Thresholds"].json;

// Rﾃｩcupﾃｩrer les statistiques
let stats = {};
if (data && data.length > 0) {
  stats = data[0];
} else {
  // Valeurs par dﾃｩfaut si aucune donnﾃｩe n'est disponible
  stats = {
    total_files: 0,
    avg_complexity: 0,
    avg_maintainability: 0,
    high_risk_files: 0,
    total_issues: 0,
    prev_total_files: 0,
    prev_avg_complexity: 0,
    prev_avg_maintainability: 0,
    prev_high_risk_files: 0,
    prev_total_issues: 0,
    top_files: []
  };
}

// Calculer les variations par rapport ﾃ la semaine prﾃｩcﾃｩdente
const percentChange = (current, previous) => {
  if (previous === 0) return 0;
  return ((current - previous) / previous) * 100;
};

const complexityChange = percentChange(stats.avg_complexity, stats.prev_avg_complexity);
const maintainabilityChange = percentChange(stats.avg_maintainability, stats.prev_avg_maintainability);
const highRiskChange = percentChange(stats.high_risk_files, stats.prev_high_risk_files);
const issuesChange = percentChange(stats.total_issues, stats.prev_total_issues);

// Fonctions pour gﾃｩnﾃｩrer des flﾃｨches de tendance
const getTrendArrow = (change, inverse = false) => {
  if (change === 0) return "竊箪

parameters:
#888888";
  if (!inverse) {
    return change > 0 ? "#f44336" : "#4caf50";
  } else {
    return change > 0 ? "#4caf50" : "#f44336";
  }
};

// Formater les tableaux pour les fichiers les plus complexes
let topFilesHtml = '';
if (stats.top_files && stats.top_files.length > 0) {
  topFilesHtml = '<table style="border-collapse: collapse; width: 100%; margin-top: 15px;">';
  topFilesHtml += `
    <thead>
      <tr style="background-color: #f2f2f2;">
        <th style="border: 1px solid #ddd; padding: 8px; text-align: left;">Fichier</th>
        <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Complexitﾃｩ</th>
        <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Maintenabilitﾃｩ</th>
        <th style="border: 1px solid #ddd; padding: 8px; text-align: center;">Problﾃｨmes</th>
      </tr>
    </thead>
    <tbody>
  `;

  stats.top_files.forEach(file => {
    const complexityColor = file.complexity > thresholds.complexityThreshold ? "#f44336" : file.complexity > 10 ? "#ff9800" : "#4caf50";
    const maintainabilityColor = file.maintainability < 50 ? "#f44336" : file.maintainability < 70 ? "#ff9800" : "#4caf50";

    topFilesHtml += `
      <tr>
        <td style="border: 1px solid #ddd; padding: 8px;">
          <a href="${thresholds.repoUrl + file.path}" target="_blank">${file.filename}</a>
        </td>
        <td style="border: 1px solid #ddd; padding: 8px; text-align: center; background-color: ${complexityColor}; color: white;">
          ${file.complexity.toFixed(1)}
        </td>
        <td style="border: 1px solid #ddd; padding: 8px; text-align: center; background-color: ${maintainabilityColor}; color: white;">
          ${file.maintainability.toFixed(1)}
        </td>
        <td style="border: 1px solid #ddd; padding: 8px; text-align: center;">
          ${file.issues_count}
        </td>
      </tr>
    `;
  });

  topFilesHtml += '</tbody></table>';
}

// Gﾃｩnﾃｩrer le contenu de l'email
const weeklyReportHtml = `
<div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto;">
  <h2 style="color: #1976d2;">沒 Rapport hebdomadaire d'analyse PHP - Semaine ${new Date().toISOString().slice(0, 10)}</h2>

  <div style="display: flex; flex-wrap: wrap; gap: 20px; margin-bottom: 30px;">
