      <div style="color: ${getTrendColor(percentChange(stats.total_files, stats.prev_total_files))}">
        ${getTrendArrow(percentChange(stats.total_files, stats.prev_total_files))} ${Math.abs(percentChange(stats.total_files, stats.prev_total_files)).toFixed(1)}%
      </div>
    </div>

    <div style="flex: 1; min-width: 200px; background-color: #f9f9f9; border-radius: 8px; padding: 15px; box-shadow: 0 2px 5px rgba(0,0,0,0.1);">
      <h3 style="margin-top: 0; color: #333;">Complexitﾃｩ moyenne</h3>
      <div style="font-size: 24px; font-weight: bold; margin-bottom: 5px;">${stats.avg_complexity.toFixed(1)}</div>
      <div style="color: ${getTrendColor(complexityChange, true)}">
        ${getTrendArrow(complexityChange, true)} ${Math.abs(complexityChange).toFixed(1)}%
      </div>
    </div>

    <div style="flex: 1; min-width: 200px; background-color: #f9f9f9; border-radius: 8px; padding: 15px; box-shadow: 0 2px 5px rgba(0,0,0,0.1);">
      <h3 style="margin-top: 0; color: #333;">Maintenabilitﾃｩ moyenne</h3>
