      <div style="color: ${getTrendColor(maintainabilityChange)}">
        ${getTrendArrow(maintainabilityChange)} ${Math.abs(maintainabilityChange).toFixed(1)}%
      </div>
    </div>

    <div style="flex: 1; min-width: 200px; background-color: #f9f9f9; border-radius: 8px; padding: 15px; box-shadow: 0 2px 5px rgba(0,0,0,0.1);">
