// Lire la configuration du pipeline
const fs = require('fs');
const path = require('path');

const configPath = '/workspaces/cahier-des-charge/config/audit-pipeline-config.json';
let config;

try {
  const configContent = fs.readFileSync(configPath, 'utf8');
  config = JSON.parse(configContent).pipelineConfig;
} catch (error) {
  throw new Error(`Erreur lors de la lecture de la configuration: ${error.message}`);
}

return {
  json: {
    pipelineConfig: config,
    timestamp: new Date().toISOString(),
    maxFilesToProcess: config.maxFilesPerRun || 5,
    highPriorityThreshold: config.highPriorityThreshold || 8
  }
};

