const output = $input.item.json.stdout || '';

try {
  // Extraction de la liste JSON des fichiers
  const jsonMatch = output.match(/\[.*?\]/s);

  if (!jsonMatch) {
    throw new Error('Aucune liste de fichiers trouv√©e dans la sortie');
  }

  const selectedFiles = JSON.parse(jsonMatch[0]);

  if (!Array.isArray(selectedFiles) || selectedFiles.length === 0) {
    return [
      {
        json: {
          message: 'Aucun fichier √† analyser pour cette ex√©cution',
          timestamp: new Date().toISOString(),
          selectedFiles: [],
          pipelineConfig: $input.item.json.pipelineConfig
        }
      }
    ];
  }

  // Retourner un item pour chaque fichier s√©lectionn√©
  return selectedFiles.map(filePath => ({
    json: {
      filePath,
      fileName: filePath.split('/').pop(),
      timestamp: new Date().toISOString(),
      batchId: new Date().toISOString().replace(/[:.]/g, '-'),
      selectionMethod: 'intelligent-selector',
      pipelineConfig: $input.item.json.pipelineConfig,
      outputDir: `/workspaces/cahier-des-charge/reports/analysis/${filePath.split('/').pop().replace('.php', '')}`
    }
  }));
} catch (error) {
  throw new Error(`Erreur lors du traitement de la sortie: ${error.message}`);
}

