// R√©cup√©rer l'agent actuel √† ex√©cuter
const currentIndex = $input.item.json.currentAgentIndex;
const agents = $input.item.json.agentsToRun;

if (currentIndex >= agents.length) {
  // Tous les agents ont √©t√© ex√©cut√©s
  return {
    json: {
      ...$input.item.json,
      pipelineCompleted: true
    }
  };
}

const currentAgent = agents[currentIndex];

return {
  json: {
    ...$input.item.json,
    currentAgent,
    currentAgentCommand: `cd /workspaces/cahier-des-charge && npx ts-node ${currentAgent.script} --file=${$input.item.json.filePath} --output-dir=${$input.item.json.outputDir}`
  }
};

// Stocker le r√©sultat de l'agent actuel
const agentId = $input.item.json.currentAgent.id;
const agentResults = $input.item.json.agentResults || {};

// Analyser la sortie pour v√©rifier si l'ex√©cution a r√©ussi
const stdout = $input.item.json.stdout || '';
const stderr = $input.item.json.stderr || '';
const exitCode = $input.item.json.exitCode;

const success = exitCode === 0;

// Mettre √† jour les r√©sultats des agents
agentResults[agentId] = {
  success,
  timestamp: new Date().toISOString(),
  stdout: stdout.slice(0, 500) + (stdout.length > 500 ? '...' : ''),
  stderr: stderr.slice(0, 500) + (stderr.length > 500 ? '...' : ''),
  outputFiles: success ? $input.item.json.currentAgent.outputFiles.map(file => `${$input.item.json.outputDir}/${file}`) : []
};

// Passer √† l'agent suivant
return {
  json: {
    ...$input.item.json,
    currentAgentIndex: $input.item.json.currentAgentIndex + 1,
    agentResults
  }
};

