// Configuration des seuils d'alerte avanc√©s
return {
  // Seuils de complexit√© pour diff√©rentes cat√©gories
  complexityThresholds: {
    critical: 25,    // Complexit√© critique n√©cessitant une action imm√©diate
    high: 15,        // Complexit√© √©lev√©e
    medium: 10       // Complexit√© moyenne
  },

  // Configuration des alertes
  alerts: {
    // Activer/d√©sactiver les canaux de notification
    channels: {
      email: true,
      slack: true,
      teams: false,
      webhook: true
    },

    // Destinataires des alertes par niveau
    recipients: {
      critical: ["cto@example.com", "tech-lead@example.com"],
      high: ["dev-team@example.com", "tech-lead@example.com"],
      all: ["quality-dashboard@example.com"]
