    // Configuration des options de regroupement
    grouping: {
      byDirectory: true,     // Regrouper les fichiers par r√©pertoire
      byComplexity: true,    // Regrouper par niveau de complexit√©
      maxFilesPerAlert: 20   // Nombre maximum de fichiers par alerte
    }
  },

  // Configuration de l'environnement
  environment: {
    dashboardUrl: "https://dashboard.example.com/php-analysis",
    repoUrl: "https://github.com/organization/project/blob/main/",
    jiraProject: "PHP",
    supabaseConfig: {
      url: process.env.SUPABASE_URL || "",
      table: "php_analysis.files"
    }
  },

  // Options avanc√©es
  advanced: {
    autoCreateTickets: true,           // Cr√©er automatiquement des tickets Jira
    intelligentGrouping: true,         // Utiliser le regroupement intelligent
    ignoreGeneratedFiles: true,        // Ignorer les fichiers g√©n√©r√©s automatiquement
    priorityCalculationAlgorithm: "complexityAndDependencies"  // Algorithme de calcul de priorit√©
  }
};

// Traitement et pr√©paration des donn√©es pour les alertes

// R√©cup√©ration des donn√©es
const files = $input.all();
const config = $node["Configuration avanc√©e"].json;

// Fonction pour calculer la priorit√© d'un fichier
const calculatePriority = (file) => {
  let score = 0;

  // Facteur principal: niveau de complexit√©
  switch(file.complexity_level) {
    case 'critical': score += 100; break;
    case 'high': score += 50; break;
    case 'medium': score += 25; break;
    default: score += 10;
  }

  // Autres facteurs d'influence
  // Nombre de lignes (les plus grands fichiers sont potentiellement plus probl√©matiques)
  score += Math.min(file.loc / 50, 20); // Maximum +20 points pour les fichiers tr√®s grands

  // Influence du r√©pertoire
  // Les r√©pertoires avec beaucoup de fichiers complexes sont prioritaires
  score += (file.directory_critical_files * 5) + (file.directory_high_files * 2);

  // Si le fichier a d√©j√† une alerte active, l√©g√®rement moins prioritaire
  if (file.has_existing_alert) {
    score -= 10;
  }

  return score;
};

// Fonction pour d√©terminer si un fichier doit √™tre ignor√©
const shouldIgnoreFile = (file) => {
  // Ignorer les fichiers g√©n√©r√©s automatiquement selon la configuration
  if (config.advanced.ignoreGeneratedFiles) {
    // Patterns courants pour les fichiers g√©n√©r√©s
    const generatedPatterns = [
      /\.generated\.php$/i,
      /\.g\.php$/i,
      /\.auto\.php$/i,
      /vendor\//i,
      /node_modules\//i
    ];

    // V√©rifier si le fichier correspond √† un pattern ignor√©
    return generatedPatterns.some(pattern => pattern.test(file.file_path));
  }

  return false;
};

// Filtrer et trier les fichiers
const processedFiles = files
  .filter(file => !shouldIgnoreFile(file))
  .map(file => {
    return {
      ...file,
      priority: calculatePriority(file)
    };
  })
  .sort((a, b) => b.priority - a.priority);

// Regrouper les fichiers
const groupFiles = () => {
  const groups = {
    critical: [],
    high: [],
    medium: []
  };

  // R√©partir par niveau de complexit√©
  processedFiles.forEach(file => {
    if (groups[file.complexity_level]) {
      groups[file.complexity_level].push(file);
    }
  });

  // Limiter le nombre de fichiers par groupe
  Object.keys(groups).forEach(level => {
    if (groups[level].length > config.alerts.grouping.maxFilesPerAlert) {
      groups[level] = groups[level].slice(0, config.alerts.grouping.maxFilesPerAlert);
    }
  });

  return groups;
};

// Cr√©er les groupes
const fileGroups = groupFiles();

// Pr√©parer les donn√©es d'alerte
const alertData = {
  hasComplexFiles: processedFiles.length > 0,
  fileCount: processedFiles.length,
  criticalCount: fileGroups.critical.length,
  highCount: fileGroups.high.length,
  mediumCount: fileGroups.medium.length,
  fileGroups,
  config,
  // G√©n√©rer les destinataires en fonction des niveaux de complexit√© d√©tect√©s
  recipients: [
    ...new Set([
      ...(fileGroups.critical.length > 0 ? config.alerts.recipients.critical : []),
      ...(fileGroups.high.length > 0 ? config.alerts.recipients.high : []),
      ...config.alerts.recipients.all
    ])
  ]
};

return alertData;

// G√©n√©ration des formats de notification
const data = $input.item.json;
const config = data.config;

// 1. G√©n√©rer le HTML pour l'email
