      `;
    }
  });

  // Ajouter les recommandations et le footer
  html += `
    <div style="margin-top: 30px; background-color: #f8f9fa; padding: 15px; border-radius: 5px;">
      <h3 style="margin-top: 0; color: #333;">Recommandations</h3>
      <ul>
        ${data.criticalCount > 0 ? '<li>R√©duisez imm√©diatement la complexit√© des fichiers critiques</li>' : ''}
        <li>Refactorisez les fonctions complexes en les divisant en fonctions plus petites</li>
        <li>Mettez en place des tests automatis√©s avant de refactoriser</li>
        <li>Consultez le <a href="${config.environment.dashboardUrl}">tableau de bord d'analyse PHP</a> pour plus de d√©tails</li>
      </ul>
    </div>

    <div style="margin-top: 30px; padding-top: 15px; border-top: 1px solid #eee; color: #777; font-size: 0.9em;">
      <p>Cette alerte a √©t√© g√©n√©r√©e automatiquement par le syst√®me d'analyse de complexit√© PHP.</p>
    </div>
  </div>
  `;

  return html;
};

// 2. G√©n√©rer les blocs Slack
const generateSlackBlocks = () => {
  const blocks = [
    {
      "type": "header",
      "text": {
        "type": "plain_text",
        "text": `${data.criticalCount > 0 ? 'üö® ALERTE CRITIQUE' : '‚ö†Ô∏è Alerte'} : Fichiers PHP complexes`,
        "emoji": true
      }
    },
    {
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": `*${data.fileCount} fichiers PHP* d√©passent les seuils de complexit√© acceptables :
${data.criticalCount > 0 ? `‚Ä¢ *${data.criticalCount} fichiers critiques* (complexit√© ‚â• ${config.complexityThresholds.critical})\n` : ''}
${data.highCount > 0 ? `‚Ä¢ *${data.highCount} fichiers √† complexit√© √©lev√©e* (complexit√© ‚â• ${config.complexityThresholds.high})\n` : ''}
${data.mediumCount > 0 ? `‚Ä¢ *${data.mediumCount} fichiers √† complexit√© moyenne* (complexit√© ‚â• ${config.complexityThresholds.medium})\n` : ''}`
      }
    },
    {
      "type": "divider"
    }
  ];

  // Ajouter les fichiers critiques en priorit√©
  if (data.criticalCount > 0) {
    blocks.push({
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "*Fichiers critiques n√©cessitant une action imm√©diate :*"
      }
    });

    data.fileGroups.critical.slice(0, 5).forEach(file => {
      blocks.push({
        "type": "section",
        "text": {
          "type": "mrkdwn",
          "text": `‚Ä¢ <${config.environment.repoUrl + file.file_path}|${file.file_path}>\n   Complexit√©: *${file.complexity_score}* | Lignes: ${file.loc}`
        }
      });
    });
  }

  // Ajouter les fichiers √† haute complexit√©
  if (data.highCount > 0 && blocks.length < 15) {
    blocks.push({
      "type": "section",
      "text": {
        "type": "mrkdwn",
        "text": "*Fichiers √† complexit√© √©lev√©e :*"
      }
    });

    data.fileGroups.high.slice(0, 3).forEach(file => {
      blocks.push({
        "type": "section",
        "text": {
          "type": "mrkdwn",
          "text": `‚Ä¢ <${config.environment.repoUrl + file.file_path}|${file.file_path}>\n   Complexit√©: *${file.complexity_score}* | Lignes: ${file.loc}`
        }
      });
    });
  }

  // Ajouter le lien vers le tableau de bord
  blocks.push({
    "type": "context",
    "elements": [
      {
        "type": "mrkdwn",
