  return blocks;
};

// 3. G√©n√©rer la structure de ticket Jira
const generateJiraTicket = () => {
  let summary = "";
  if (data.criticalCount > 0) {
    summary = `[URGENT] Refactoriser ${data.criticalCount} fichiers PHP critiques`;
  } else if (data.highCount > 0) {
    summary = `Refactoriser ${data.highCount} fichiers PHP √† complexit√© √©lev√©e`;
  } else {
    summary = `Am√©liorer ${data.mediumCount} fichiers PHP √† complexit√© moyenne`;
  }

  // Construire la description
  let description = `h2. Alerte de complexit√© de code PHP\n\n`;
  description += `L'analyse de code a identifi√© *${data.fileCount} fichiers PHP* d√©passant les seuils de complexit√© acceptables.\n\n`;

  if (data.criticalCount > 0) {
    description += `h3. Fichiers critiques (${data.criticalCount})\n`;
    data.fileGroups.critical.forEach(file => {
      description += `* [${file.file_path}|${config.environment.repoUrl + file.file_path}] - Complexit√©: *${file.complexity_score}* - Lignes: ${file.loc}\n`;
    });
    description += "\n";
  }

  if (data.highCount > 0) {
    description += `h3. Fichiers √† complexit√© √©lev√©e (${data.highCount})\n`;
    data.fileGroups.high.forEach(file => {
      description += `* [${file.file_path}|${config.environment.repoUrl + file.file_path}] - Complexit√©: *${file.complexity_score}* - Lignes: ${file.loc}\n`;
    });
    description += "\n";
  }

  description += `h3. Recommandations\n`;
  description += `* Refactoriser les fichiers critiques en priorit√©\n`;
  description += `* Diviser les fonctions complexes en fonctions plus petites\n`;
  description += `* Ajouter des tests unitaires avant la refactorisation\n`;
  description += `* Consulter le [tableau de bord d'analyse|${config.environment.dashboardUrl}] pour plus de d√©tails\n`;

  // Structure du ticket
  return {
    fields: {
      project: { key: config.environment.jiraProject },
      summary: summary,
      description: description,
      issuetype: { name: data.criticalCount > 0 ? "Bug" : "Task" },
      priority: { name: data.criticalCount > 0 ? "Highest" : data.highCount > 0 ? "High" : "Medium" },
      labels: ["refactoring", "php", "code-quality", "technical-debt"]
