  };
};

// 4. G√©n√©rer les donn√©es pour enregistrer les alertes dans Supabase
const generateAlertRecords = () => {
  const alerts = [];

  // Pour chaque fichier dans chaque groupe
  Object.keys(data.fileGroups).forEach(level => {
    data.fileGroups[level].forEach(file => {
      if (!file.has_existing_alert) {
        alerts.push({
          file_id: file.id,
          alert_type: "complexity",
          severity: level,
          message: `Complexit√© cyclomatique √©lev√©e (${file.complexity_score})`,
          line_number: null, // Pas de ligne sp√©cifique pour la complexit√© globale
          created_at: new Date().toISOString(),
          resolved: false
        });
      }
    });
  });

  return alerts;
};

// G√©n√©ration de tous les formats
return {
  hasComplexFiles: data.hasComplexFiles,
  emailHtml: generateEmailHtml(),
  slackBlocks: generateSlackBlocks(),
  jiraTicket: generateJiraTicket(),
  alertRecords: generateAlertRecords(),
  recipients: data.recipients,
  config: data.config,
  fileCount: data.fileCount,
  criticalCount: data.criticalCount,
  highCount: data.highCount,
  mediumCount: data.mediumCount
};

// Aucun fichier complexe d√©tect√©
return {
  message: "Analyse termin√©e : aucun fichier PHP complexe d√©tect√© au-dessus des seuils configur√©s.",
  date: new Date().toISOString(),
  thresholds: $node["Configuration avanc√©e"].json.complexityThresholds
};
