// VÃ©rifier si l'agent est activÃ©
const config = JSON.parse(items[0].json.value);

if (!config.agents || !config.agents.analyseRelationnelle || !config.agents.analyseRelationnelle.enabled) {
  return [
    {
      json: {
        success: false,
        message: "L'agent Analyse Relationnelle est dÃ©sactivÃ© dans la configuration"
      }
    }
  ];
}

// VÃ©rifier si les fichiers d'entrÃ©e existent
const fs = require('fs');
const schemaPath = `${config.baseOutputPath}/schema_raw.json`;

if (!fs.existsSync(schemaPath)) {
  return [
    {
      json: {
        success: false,
        message: `Le fichier schema_raw.json n'existe pas au chemin ${schemaPath}. ExÃ©cutez d'abord l'agent Extracteur.`
      }
    }
  ];
}

return [
  {
    json: {
      success: true,
      config,
      schemaPath
    }
  }
];

// Fusionner les rÃ©sultats
const graphData = JSON.parse(items[0].json.value);
const auditContent = items[1].json.value;

// Extraire des statistiques intÃ©ressantes du graphe
const relations = graphData.relations || [];
const relationTypes = {
  explicit: relations.filter(r => r.confidence === 1.0).length,
  implicit: relations.filter(r => r.confidence < 1.0 && !r.is_polymorphic).length,
  polymorphic: relations.filter(r => r.is_polymorphic).length
};

// Identifier les problÃ¨mes et risques
const warnings = relations.filter(r => r.warning).length;
const polymorphicTables = [...new Set(relations.filter(r => r.is_polymorphic).map(r => r.from_table))];

// Rechercher les tables centrales
const centralTables = graphData.graph.nodes
  .filter(n => n.centrality > 0.1)
  .sort((a, b) => b.centrality - a.centrality)
  .slice(0, 3)
  .map(n => n.label);

return [
  {
    json: {
      success: true,
      message: "Analyse relationnelle terminÃ©e avec succÃ¨s",
      stats: {
        totalTables: graphData.graph.nodes.length,
        totalRelations: relations.length,
        explicitRelations: relationTypes.explicit,
        implicitRelations: relationTypes.implicit,
        polymorphicRelations: relationTypes.polymorphic,
        warnings: warnings,
        polymorphicTables: polymorphicTables,
        centralTables: centralTables
      },
      graphJson: items[0].json.value,
      auditMarkdown: auditContent,
      prismaRecommendations: relations.map(r => ({
        fromTable: r.from_table,
        relationName: r.prisma_relation.relation_name,
        toTable: r.to_table,
        relationCode: `${r.prisma_relation.relation_name}: ${r.to_table} @relation(fields: [${r.from_column}], references: [${r.to_column}]${r.prisma_relation.onDelete ? ', onDelete: ' + r.prisma_relation.onDelete : ''})`
      }))
    }
  }
];

// VÃ©rifier s'il existe un fichier polymorphic_candidates.json
const fs = require('fs');
const path = require('path');
const polymorphicPath = path.join(items[0].json.config.baseOutputPath, 'polymorphic_candidates.json');

if (fs.existsSync(polymorphicPath)) {
  const polymorphicData = JSON.parse(fs.readFileSync(polymorphicPath, 'utf8'));

  return [
    {
      json: {
        ...items[0].json,
        polymorphicCandidates: polymorphicData
      }
    }
  ];
}

return items;

