// D√©terminer les param√®tres d'analyse
const now = new Date();
const timestamp = now.toISOString().replace(/:/g, '-');
let analysisMode = 'full';
let targetPath = '/workspaces/cahier-des-charge/src';
let analysisOptions = {};

// Analyser les donn√©es de webhook si pr√©sentes
if ($json.body && typeof $json.body === 'object') {
  analysisMode = $json.body.mode || 'full';
  targetPath = $json.body.targetPath || targetPath;
  analysisOptions = $json.body.options || {};
}

// Valider et ajuster les valeurs
const validModes = ['quick', 'full', 'deep', 'dependency', 'structure'];
if (!validModes.includes(analysisMode)) {
  analysisMode = 'full';
}

// G√©n√©rer un ID d'analyse unique
const analysisId = `php-analysis-${timestamp}-${analysisMode}`;

// Pr√©parer les options de ligne de commande
const optionsFlags = [];
if (analysisOptions.includeTests === true) optionsFlags.push('--include-tests');
if (analysisOptions.skipVendor !== false) optionsFlags.push('--skip-vendor');
if (analysisOptions.analyzeDependencies === true) optionsFlags.push('--analyze-dependencies');
if (analysisOptions.generateGraph === true) optionsFlags.push('--generate-graph');
if (analysisOptions.analyzeRoutes === true) optionsFlags.push('--analyze-routes');

return {
  json: {
    analysisId,
    analysisMode,
    targetPath,
    analysisOptions,
    optionsFlags: optionsFlags.join(' '),
    timestamp,
    reportPath: `/workspaces/cahier-des-charge/reports/php-analysis/report-${timestamp}.json`,
    summaryPath: `/workspaces/cahier-des-charge/reports/php-analysis/summary-${timestamp}.md`
  }
};

// Extraire et analyser les r√©sultats de l'analyse PHP
let analysisResults = {};
let summaryData = {
  fileCount: 0,
  classCount: 0,
  functionCount: 0,
  loc: 0,
  complexityScore: 0,
  databaseQueries: 0,
  potentialIssues: [],
  dependencies: [],
  migrationComplexity: 'medium',
  recommendations: []
};

try {
  // Lire le fichier de rapport JSON
  const fs = require('fs');
  if (fs.existsSync($json.reportPath)) {
    const reportContent = fs.readFileSync($json.reportPath, 'utf8');
    analysisResults = JSON.parse(reportContent);

    // Extraire les donn√©es de base
    summaryData.fileCount = analysisResults.fileCount || analysisResults.stats?.fileCount || 0;
    summaryData.classCount = analysisResults.classCount || analysisResults.stats?.classCount || 0;
    summaryData.functionCount = analysisResults.functionCount || analysisResults.stats?.functionCount || 0;
    summaryData.loc = analysisResults.loc || analysisResults.stats?.loc || 0;

    // Extraire la complexit√© et les probl√®mes
    summaryData.complexityScore = analysisResults.complexityScore || analysisResults.metrics?.complexity || 0;
    summaryData.databaseQueries = analysisResults.databaseQueries?.length || analysisResults.database?.queryCount || 0;

    // Extraire les probl√®mes potentiels
    if (analysisResults.issues && Array.isArray(analysisResults.issues)) {
      summaryData.potentialIssues = analysisResults.issues
        .sort((a, b) => (b.severity || 0) - (a.severity || 0))
        .slice(0, 10);
    }

    // Extraire les d√©pendances
    if (analysisResults.dependencies && Array.isArray(analysisResults.dependencies)) {
      summaryData.dependencies = analysisResults.dependencies
        .map(dep => typeof dep === 'string' ? { name: dep } : dep)
        .slice(0, 20);
    }

    // D√©terminer la complexit√© de migration
    const complexityFactors = {
      fileCount: summaryData.fileCount > 1000 ? 3 : summaryData.fileCount > 500 ? 2 : 1,
      complexityScore: summaryData.complexityScore > 70 ? 3 : summaryData.complexityScore > 40 ? 2 : 1,
      issueCount: (analysisResults.issues?.length || 0) > 100 ? 3 : (analysisResults.issues?.length || 0) > 50 ? 2 : 1,
      databaseComplexity: (analysisResults.database?.complexity || 0) > 7 ? 3 : (analysisResults.database?.complexity || 0) > 4 ? 2 : 1
    };

    const avgComplexity = Object.values(complexityFactors).reduce((sum, val) => sum + val, 0) / Object.values(complexityFactors).length;

    if (avgComplexity > 2.5) {
      summaryData.migrationComplexity = 'very-high';
    } else if (avgComplexity > 2) {
      summaryData.migrationComplexity = 'high';
    } else if (avgComplexity > 1.5) {
      summaryData.migrationComplexity = 'medium';
    } else {
      summaryData.migrationComplexity = 'low';
    }

    // G√©n√©rer des recommandations
    if (summaryData.fileCount > 1000) {
      summaryData.recommendations.push('Envisager une approche de migration progressive en priorisant les modules cl√©s');
    }

    if (summaryData.complexityScore > 60) {
      summaryData.recommendations.push('Refactoriser le code complexe avant la migration pour simplifier le processus');
    }

    if (summaryData.databaseQueries > 100) {
      summaryData.recommendations.push('Utiliser une couche d\'abstraction de base de donn√©es pour faciliter la transition');
    }

    // Ajouter des recommandations sp√©cifiques des analyseurs
    if (analysisResults.recommendations && Array.isArray(analysisResults.recommendations)) {
      summaryData.recommendations = [...summaryData.recommendations, ...analysisResults.recommendations.slice(0, 5)];
    }
  } else {
    throw new Error(`Le fichier de rapport ${$json.reportPath} n'existe pas`);
  }
} catch (error) {
  summaryData.error = `Erreur lors du traitement des r√©sultats: ${error.message}`;
}

return {
  json: {
    ...$json,
    analysisResults: {
      summary: summaryData,
      details: analysisResults
    }
  }
};

