// Traiter les r√©sultats de la d√©couverte SEO
const output = $input.item.json.stdout || '';
const outputDir = '/workspaces/cahier-des-charge/reports/analysis/seo';
const timestamp = new Date().toISOString();
const fs = require('fs');
const path = require('path');

try {
  // Extraire les donn√©es JSON du r√©sultat
  const jsonMatch = output.match(/\{[\s\S]*\}/);

  if (!jsonMatch) {
    throw new Error('Aucune donn√©e JSON trouv√©e dans la sortie');
  }

  const seoData = JSON.parse(jsonMatch[0]);

  // Traitement et enrichissement des donn√©es
  const processedData = {
    timestamp,
    summary: {
      urlsAnalyzed: seoData.urls ? seoData.urls.length : 0,
      metaTitleCount: 0,
      metaDescriptionCount: 0,
      h1Count: 0,
      canonicalLinksCount: 0,
      hreflangCount: 0,
      structuredDataCount: 0,
      robotsTxtCount: seoData.robotsTxt ? 1 : 0,
      sitemapCount: seoData.sitemaps ? seoData.sitemaps.length : 0
    },
    pages: [],
    sitemaps: seoData.sitemaps || [],
    robotsTxt: seoData.robotsTxt || null,
    internalLinks: seoData.internalLinks || [],
    externalLinks: seoData.externalLinks || [],
    seoIssues: [],
    migrationRisks: []
  };

  // Traiter chaque URL d√©couverte
  if (seoData.urls && Array.isArray(seoData.urls)) {
    seoData.urls.forEach(url => {
      // Enrichir les donn√©es de la page
      const pageData = {
        url: url.url,
        path: url.path,
        metaTitle: url.metaTitle,
        metaDescription: url.metaDescription,
        h1: url.h1,
        h2Count: url.h2Count || 0,
        h3Count: url.h3Count || 0,
        imagesCount: url.imagesCount || 0,
        imagesWithAlt: url.imagesWithAlt || 0,
        wordCount: url.wordCount || 0,
        responseCode: url.responseCode || 200,
        canonicalLink: url.canonicalLink,
        hreflang: url.hreflang || [],
        structuredData: url.structuredData || []
      };

      // Mettre √† jour les compteurs du r√©sum√©
      if (url.metaTitle) processedData.summary.metaTitleCount++;
      if (url.metaDescription) processedData.summary.metaDescriptionCount++;
      if (url.h1) processedData.summary.h1Count++;
      if (url.canonicalLink) processedData.summary.canonicalLinksCount++;
      if (url.hreflang && url.hreflang.length > 0) processedData.summary.hreflangCount++;
      if (url.structuredData && url.structuredData.length > 0) processedData.summary.structuredDataCount++;

      // V√©rifier les probl√®mes SEO courants
      if (!url.metaTitle) {
        processedData.seoIssues.push({
          url: url.url,
          issue: 'Titre meta manquant',
          severity: 'high'
        });
      } else if (url.metaTitle.length < 10 || url.metaTitle.length > 60) {
        processedData.seoIssues.push({
          url: url.url,
          issue: `Longueur du titre meta non optimale (${url.metaTitle.length} caract√®res)`,
          severity: 'medium'
        });
      }

      if (!url.metaDescription) {
        processedData.seoIssues.push({
          url: url.url,
          issue: 'Description meta manquante',
          severity: 'medium'
        });
      } else if (url.metaDescription.length < 50 || url.metaDescription.length > 160) {
        processedData.seoIssues.push({
          url: url.url,
          issue: `Longueur de la description meta non optimale (${url.metaDescription.length} caract√®res)`,
          severity: 'low'
        });
      }

      if (!url.h1) {
        processedData.seoIssues.push({
          url: url.url,
          issue: 'Balise H1 manquante',
          severity: 'high'
        });
      }

      if (url.imagesCount > 0 && url.imagesWithAlt < url.imagesCount) {
        processedData.seoIssues.push({
          url: url.url,
          issue: `${url.imagesCount - url.imagesWithAlt}/${url.imagesCount} images sans attribut alt`,
          severity: 'medium'
        });
      }

      // Ajouter la page aux donn√©es trait√©es
      processedData.pages.push(pageData);
    });
  }

  // Identifier les risques de migration
  if (seoData.urls && seoData.urls.some(url => url.canonicalLink)) {
    processedData.migrationRisks.push({
      risk: 'Liens canoniques √† pr√©server pendant la migration',
      mitigation: 'Impl√©menter des r√®gles dans Remix pour maintenir les m√™mes liens canoniques'
    });
  }

  if (seoData.urls && seoData.urls.some(url => url.hreflang && url.hreflang.length > 0)) {
    processedData.migrationRisks.push({
      risk: 'Balises hreflang (internationalisation) √† pr√©server',
      mitigation: 'Configurer correctement les param√®tres r√©gionaux dans l\'application Remix'
    });
  }

  if (seoData.robotsTxt) {
    processedData.migrationRisks.push({
      risk: 'R√®gles robots.txt √† maintenir',
      mitigation: 'Copier les r√®gles dans le nouveau robots.txt ou impl√©menter via API Remix'
    });
  }

  if (seoData.redirects && seoData.redirects.length > 0) {
    processedData.migrationRisks.push({
      risk: `${seoData.redirects.length} redirections √† pr√©server`,
      mitigation: 'Impl√©menter les redirections dans les routes Remix ou au niveau serveur'
    });
  }

  // Sauvegarder les donn√©es trait√©es
  const processedDataPath = path.join(outputDir, `seo_analysis_${timestamp.replace(/:/g, '-')}.json`);
  fs.writeFileSync(processedDataPath, JSON.stringify(processedData, null, 2));

  return {
    json: {
      ...processedData,
      analysisPath: processedDataPath,
      outputDir
    }
  };
} catch (error) {
  return {
    json: {
      error: error.message,
      timestamp: new Date().toISOString()
    }
  };
}

parameters.functionCode:
// Consolider les r√©sultats des diff√©rentes g√©n√©rations
const analysis = $input.item.json;
const timestamp = new Date().toISOString();

// Calculer les m√©triques globales
const metrics = {
  totalUrls: analysis.summary.urlsAnalyzed,
  seoIssuesCount: analysis.seoIssues.length,
  migrationRisksCount: analysis.migrationRisks.length,
  seoScore: 0,
  highPriorityIssues: analysis.seoIssues.filter(issue => issue.severity === 'high').length,
  mediumPriorityIssues: analysis.seoIssues.filter(issue => issue.severity === 'medium').length,
  lowPriorityIssues: analysis.seoIssues.filter(issue => issue.severity === 'low').length
};

// Calculer un score SEO simple (0-100)
if (metrics.totalUrls > 0) {
  const metaTitleScore = (analysis.summary.metaTitleCount / metrics.totalUrls) * 30;
  const metaDescScore = (analysis.summary.metaDescriptionCount / metrics.totalUrls) * 20;
  const h1Score = (analysis.summary.h1Count / metrics.totalUrls) * 20;

  // P√©nalit√©s bas√©es sur les probl√®mes
  const highIssuePenalty = Math.min(20, metrics.highPriorityIssues * 2);
  const mediumIssuePenalty = Math.min(10, metrics.mediumPriorityIssues);

  metrics.seoScore = Math.round(metaTitleScore + metaDescScore + h1Score + 30 - highIssuePenalty - mediumIssuePenalty);
}

// D√©terminer le niveau de complexit√© de la migration SEO
let migrationComplexity = 'faible';
if (metrics.highPriorityIssues > 5 || metrics.migrationRisksCount > 3) {
  migrationComplexity = '√©lev√©e';
} else if (metrics.highPriorityIssues > 0 || metrics.migrationRisksCount > 0) {
  migrationComplexity = 'moyenne';
}

return {
  json: {
    ...analysis,
    metrics,
    migrationComplexity,
    consolidatedTimestamp: timestamp
  }
};

