// R√©cup√©ration du statut de la base de donn√©es
const { exec } = require('child_process');

return new Promise((resolve, reject) => {
  exec('docker-compose -f docker-compose.dev.yml exec -T postgres-intermediate psql -U postgres -d app_migration -c "SELECT COUNT(*) FROM information_schema.tables WHERE table_schema = \'public\';"', (error, stdout, stderr) => {
    if (error) {
      return resolve({
        json: {
          success: false,
          error: error.message,
          stage: 'database-check',
          timestamp: new Date().toISOString()
        }
      });
    }

    // Extraction du nombre de tables
    const match = stdout.match(/(\d+)/);
    const tableCount = match ? parseInt(match[1], 10) : 0;

    resolve({
      json: {
        success: true,
        tableCount,
        stage: 'database-check',
        timestamp: new Date().toISOString(),
        event: $input.item.json.event || 'manual',
        source: $input.item.json.source || 'n8n'
      }
    });
  });
});

