// Analyser la sortie du validateur
const stdout = $input.item.json.stdout || '';
const stderr = $input.item.json.stderr || '';

try {
  // Extraction des r√©sultats JSON si pr√©sents
  const jsonMatch = stdout.match(/\{[\s\S]*\}/m);
  let validationResults;

  if (jsonMatch) {
    validationResults = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('Aucun r√©sultat JSON trouv√© dans la sortie');
  }

  // Calculer des m√©triques suppl√©mentaires
  const totalTables = validationResults.tables.length;
  const validTables = validationResults.tables.filter(t => t.status === 'valid').length;
  const invalidTables = validationResults.tables.filter(t => t.status === 'invalid').length;
  const notMigrated = validationResults.tables.filter(t => t.status === 'not_migrated').length;

  const successRate = totalTables > 0 ? Math.round((validTables / totalTables) * 100) : 0;
  const issues = validationResults.tables
    .filter(t => t.issues && t.issues.length > 0)
    .map(t => ({
      table: t.name,
      issueCount: t.issues.length,
      issues: t.issues.slice(0, 3) // Limiter √† 3 issues par table pour √©viter les messages trop longs
    }));

  return {
    json: {
      success: validationResults.success,
      timestamp: new Date().toISOString(),
      totalTables,
      validTables,
      invalidTables,
      notMigrated,
      successRate,
      issues,
      event: $input.item.json.event,
      source: $input.item.json.source,
      rawResults: validationResults
    }
  };
} catch (error) {
  return {
    json: {
      success: false,
      error: error.message,
      stdout: stdout.slice(0, 500) + (stdout.length > 500 ? '...' : ''),
      stderr: stderr.slice(0, 500) + (stderr.length > 500 ? '...' : ''),
      timestamp: new Date().toISOString(),
      event: $input.item.json.event,
      source: $input.item.json.source
    }
  };
}

