// G√©n√©rer le fichier de signature pour la migration valid√©e
const fs = require('fs');
const path = require('path');

const validationStatus = {
  timestamp: $input.item.json.timestamp,
  success: true,
  successRate: $input.item.json.success_rate,
  validatedBy: 'n8n-workflow',
  tables: {
    total: $input.item.json.total_tables,
    valid: $input.item.json.valid_tables,
    invalid: $input.item.json.invalid_tables,
    notMigrated: $input.item.json.not_migrated
  },
  signature: `migration-validated-${new Date().toISOString().replace(/[:.]/g, '-')}`,
  metadata: {
    event: $input.item.json.event,
    source: $input.item.json.source,
    issuesCount: $input.item.json.issues_count
  }
};

const signatureFilePath = '/workspaces/cahier-des-charge/reports/migration_signature.json';

try {
  // Cr√©er le dossier reports s'il n'existe pas
  const dir = path.dirname(signatureFilePath);
  if (!fs.existsSync(dir)) {
    fs.mkdirSync(dir, { recursive: true });
  }

  // √âcrire le fichier de signature
  fs.writeFileSync(signatureFilePath, JSON.stringify(validationStatus, null, 2));

  return {
    json: {
      ...validationStatus,
      signatureFilePath,
      signatureCreated: true
    }
  };
} catch (error) {
  return {
    json: {
      ...$input.item.json,
      error: error.message,
      signatureCreated: false
    }
  };
}

