// Formater l'erreur pour la notification et le log
const workflow = $workflow;
const error = $input.item.json.error;
const timestamp = new Date().toISOString();
const errorNodeId = $input.item.json.lastNodeExecuted;

return {
  json: {
    workflow: {
      id: workflow.id,
      name: workflow.name
    },
    error: {
      message: error.message || 'Erreur inconnue',
      stack: error.stack,
      timestamp,
      lastNodeExecuted: errorNodeId
    },
    context: {
      environment: process.env.NODE_ENV || 'development',
      timestamp
    }
  }
};

